import React from "react";
import img1 from './../Img/Imagen1.png';
import img2 from './../Img/Imagen2.png';
import img3 from './../Img/Imagen3.png';
import {ReactComponent as FlechaIzquierda} from './../Img/Izquierda.svg';
import {ReactComponent as FlechaDerecha} from './../Img/Derecha.svg';
import styled from "styled-components";


const Slider = () => {
    return (
        <ContenedorPrincipal>
            <ContenedorSlides>
                <Slide>
                    <a href = "https://www.grupobancolombia.com/personas">
                        <img src = {img1} alt=""/>
                    </a>

                </Slide>
                <Slide>
                    <a href = "https://www.grupobancolombia.com/personas">
                        <img src = {img2} alt=""/>
                    </a>

                </Slide>
                <Slide>
                    <a href = "https://www.grupobancolombia.com/personas">
                        <img src = {img3} alt=""/>
                    </a>

                </Slide>
            </ContenedorSlides>
            <Controles>
                <Boton izquierdo>
                    <FlechaIzquierda />
                </Boton>
                <Boton derecho>
                    <FlechaDerecha />
                </Boton>
            </Controles>
        </ContenedorPrincipal>
    )
}

const ContenedorPrincipal = styled.div`
Position: relative;
`;
const ContenedorSlides = styled.div`
display: flex;
flex-wrap: nowrap;
`;
const Slide = styled.div`
min-width: 100%;
overflow: hidden;
transition: .3s ease all;
z-index: 9;
position: relative;

img{
    width: 100%;
    vertical-align: top;
}
`;

const Controles = styled.div`
position: absolute;
top: 0;
z-index: 18;
width: 100%;
height: 100%;
pointer-events: none;
`;
const Boton = styled.div`
pointer-events: all;
background: none;
border: none;
cursor: pointer;
outline: none;
width: 50px;
height: 100%;
text-align: center;
position: absolute;
transition: .3s ease all;
${props => props.derecho ? 'right: 0': 'left: 0'}
`;
export default Slider;
